import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import { faMicrophone, faMicrophoneSlash } from '@fortawesome/free-solid-svg-icons';
import { useEffect, useState } from 'react';

import Dictaphone from './DIctaphone';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

export default function WebSocket({ socket }) {
	const [message, setMessage] = useState('');
	const [messages, setMessages] = useState([]);
  
	  const {
	  transcript,
	  listening,
	} = useSpeechRecognition();


  useEffect(()=>{
    if(listening) {
      SpeechRecognition.startListening();
      setMessage(transcript)
    } else {
      SpeechRecognition.stopListening()
    }
  }, [listening, transcript])

	const handleText = (e) => {
		const inputMessage = e.target.value;
		setMessage(inputMessage);
	};

	const handleSubmit = () => {
		if (!message) {
			return;
		}
		socket.emit('data', message);
		setMessage('');
	};

	useEffect(() => {
		socket.on('data', (data) => {
			let message = { ...data, time: new Date() };
			setMessages([...messages, message]);
		});
		return () => {
			socket.off('data', () => {
				console.log('data event was removed');
			});
		};
	}, [socket, messages]);

	return (
		<div>
			<section className="msger">
				<header className="msger-header">
					<div className="msger-header-title">
						<i className="fas fa-comment-alt"></i> SimpleChat
					</div>
					<div className="msger-header-options">
						<span>
							<i className="fas fa-cog"></i>
						</span>
					</div>
				</header>

				<main className="msger-chat">
					{messages.map((message, ind) => {
						return (
							<div
								className={socket.id === message.id ? 'msg right-msg' : 'msg left-msg'}
								key={ind}
							>
								<div className="msg-img"></div>
								<div className="msg-bubble">
									<div className="msg-info">
										<div className="msg-info-name">{message.id}</div>
										<div className="msg-info-time">
											{message.time.getHours() + ':' + message.time.getMinutes()}
										</div>
									</div>
									<div className="msg-text">{message.data}</div>
								</div>
							</div>
						);
					})}
				</main>
				<div className="msger-inputarea">
					<input
						type="text"
						className="msger-input"
						placeholder="Enter your message..."
						value={message}
						onChange={handleText}
					/>
					<button className="msger-send-btn" onClick={handleSubmit}>
						Send
					</button>
          <FontAwesomeIcon
					icon={
            faMicrophone
					}
				/>
				</div>
			</section>
		</div>
	);
}
