import { useEffect, useState } from "react";

export default function WebSocket({ socket }) {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [id,setId] = useState('')

  const handleText = (e) => {
    const inputMessage = e.target.value;
    setMessage(inputMessage);
  };

  const handleSubmit = () => {
    if (!message) {
      return;
    }
    socket.emit("data", message);
    setMessage("");
  };

  useEffect(() => {
    socket.on("data", (data) => {
      setMessages([...messages, data]);
    });
    return () => {
      socket.off("data", () => {
        console.log("data event was removed");
      });
    };
  }, [socket, messages]);

  return (
    <div>
      <h2>WebSocket Communication</h2>
      <input type="text" value={message} onChange={handleText} />
      <button onClick={handleSubmit}>submit</button>


      <section className="msger">
      <header className="msger-header">
    <div className="msger-header-title">
      <i className="fas fa-comment-alt"></i> SimpleChat
    </div>
    <div className="msger-header-options">
      <span><i className="fas fa-cog"></i></span>
    </div>
  </header>

  <main className="msger-chat">
    <div className="msg left-msg">
      <div
       className="msg-img"
      ></div>
      <div className="msg-bubble">
        <div className="msg-info">
          <div className="msg-info-name">BOT</div>
          <div className="msg-info-time">12:45</div>
        </div>
        <div className="msg-text">
          Hi, welcome to SimpleChat! Go ahead and send me a message. ðŸ˜„
        </div>
      </div>
    </div>

  </main>

  <form className="msger-inputarea">
    <input type="text" className="msger-input" placeholder="Enter your message..." value={message} onChange={handleText}/>
    <button type="submit" className="msger-send-btn">Send</button>
  </form>
</section>
      
      <ul>
        {messages.map((message, ind) => {
          return <li key={ind} style={socket.id===message.id ? {color:'green'}: {color:'red'}}>{message.data}</li>;
        })}
      </ul>
    </div>
  );
}
